        }

        if (thisKind == JsonValueKind.Object)
        {
            int count = 0;
            foreach (JsonObjectProperty property in this.EnumerateObject())
            {
                if (!other.TryGetProperty(property.Name, out JsonAny value) || !property.Value.Equals(value))
