        private static ImmutableDictionary<string, PropertyValidator<OneOf1Entity>> CreateLocalPropertyValidators()
        {
            ImmutableDictionary<string, PropertyValidator<OneOf1Entity>>.Builder builder =
                ImmutableDictionary.CreateBuilder<string, PropertyValidator<OneOf1Entity>>();

                    builder.Add(
                FooJsonPropertyName, __CorvusValidateFoo);
                    builder.Add(
                BazJsonPropertyName, __CorvusValidateBaz);
        
            return builder.ToImmutable();
        }

                private static ValidationContext __CorvusValidateFoo(in OneOf1Entity that, in ValidationContext validationContext, ValidationLevel level)
        {
            Corvus.Json.JsonAny property = that.Foo;
            return property.Validate(validationContext, level);
        }
                private static ValidationContext __CorvusValidateBaz(in OneOf1Entity that, in ValidationContext validationContext, ValidationLevel level)
        {
            Corvus.Json.JsonAny property = that.Baz;
            return property.Validate(validationContext, level);
        }
            
            /// <summary>
        /// Gets the value as a <see cref="JsonObject"/>.
        /// </summary>
        private JsonObject AsObject
        {
            get
            {
