        private static ImmutableDictionary<string, PropertyValidator<SubItemValue>> CreateLocalPropertyValidators()
        {
            ImmutableDictionary<string, PropertyValidator<SubItemValue>>.Builder builder =
                ImmutableDictionary.CreateBuilder<string, PropertyValidator<SubItemValue>>();

                    builder.Add(
                FooJsonPropertyName, __CorvusValidateFoo);
        
            return builder.ToImmutable();
        }

                private static ValidationContext __CorvusValidateFoo(in SubItemValue that, in ValidationContext validationContext, ValidationLevel level)
        {
            Corvus.Json.JsonAny property = that.Foo;
            return property.Validate(validationContext, level);
        }
            
    
    
            private ValidationContext ValidateObject(JsonValueKind valueKind, in ValidationContext validationContext, ValidationLevel level)
        {
            ValidationContext result = validationContext;

            if (valueKind != JsonValueKind.Object)
            {
                return result;
            }

                    int propertyCount = 0;
        
                                bool foundFoo = false;
                    
            foreach (Property property in this.EnumerateObject())
            {
                string propertyName = property.Name;

        
                        if (__CorvusLocalProperties.TryGetValue(propertyName, out PropertyValidator<SubItemValue>? propertyValidator))
                {
                    result = result.WithLocalProperty(propertyCount);
                    var propertyResult = propertyValidator(this, result.CreateChildContext(), level);
                    result = result.MergeResults(propertyResult.IsValid, level, propertyResult);
                    if (level == ValidationLevel.Flag && !result.IsValid)
                    {
                        return result;
                    }

            
                if (FooJsonPropertyName.Equals(propertyName))
                {
                    foundFoo = true;
                }
            
                }
        
        
        
        
        
        
                
                propertyCount++;

                    }

        
                        if (!foundFoo)
            {
                if (level >= ValidationLevel.Detailed)
                {
                    result = result.WithResult(isValid: false, $"6.5.3. required - required property \"foo\" not present.");
                }
                else if (level >= ValidationLevel.Basic)
                {
                    result = result.WithResult(isValid: false, "6.5.3. required - required property not present.");
                }
                else
                {
                    return result.WithResult(isValid: false);
                }
            }
                    
        
        
            return result;
        }

    
            

            

            

            

    
    
    
            
        private ValidationContext ValidateType(JsonValueKind valueKind, in ValidationContext validationContext, ValidationLevel level)
        {
            ValidationContext result = validationContext;
            bool isValid = false;

        
                
            ValidationContext localResultObject = Corvus.Json.Validate.TypeObject(valueKind, result, level);
            if (level == ValidationLevel.Flag && localResultObject.IsValid)
            {
                return validationContext;
            }

            if (localResultObject.IsValid)
            {
                isValid = true;
            }

        
        
        
        
        
        
            result = result.MergeResults(
                isValid,
                level
        
                
                , localResultObject
        
        
        
        
        
                        );

            return result;
        }

    
    
    
